contracts/src/IWaveWarzBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IWaveWarzBase
 * @notice Interface for WaveWarz Base battle platform
 */
interface IWaveWarzBase {
    // Events
    event BattleCreated(
        uint64 indexed battleId,
        address artistAWallet,
        address artistBWallet,
        uint64 startTime,
        uint64 endTime,
        address paymentToken
    );

    event SharesPurchased(
        uint64 indexed battleId,
        address indexed trader,
        bool artistA,
        uint256 tokenAmount,
        uint256 paymentAmount,
        uint256 artistFee,
        uint256 platformFee
    );

    event SharesSold(
        uint64 indexed battleId,
        address indexed trader,
        bool artistA,
        uint256 tokenAmount,
        uint256 paymentReceived,
        uint256 artistFee,
        uint256 platformFee
    );

    event BattleEnded(
        uint64 indexed battleId,
        bool winnerIsArtistA,
        uint256 artistAPool,
        uint256 artistBPool
    );

    event SharesClaimed(
        uint64 indexed battleId,
        address indexed trader,
        uint256 amountReceived
    );

    // Structs
    struct Battle {
        uint64 battleId;
        uint64 startTime;
        uint64 endTime;
        address artistAWallet;
        address artistBWallet;
        address wavewarzWallet;
        uint256 artistAPool;
        uint256 artistBPool;
        uint256 artistASupply;
        uint256 artistBSupply;
        bool winnerDecided;
        bool winnerIsArtistA;
        bool isActive;
        address paymentToken;
        address admin;
    }

    struct BattleInitParams {
        uint64 battleId;
        uint64 battleDuration;
        uint64 startTime;
        address artistAWallet;
        address artistBWallet;
        address wavewarzWallet;
        address paymentToken;
    }

    // View functions
    function getBattle(uint64 battleId) external view returns (Battle memory);
    function getArtistAToken(uint64 battleId) external view returns (address);
    function getArtistBToken(uint64 battleId) external view returns (address);
    function calculateBuyPrice(uint256 currentSupply, uint256 tokensToMint) external pure returns (uint256);
    function calculateSellReturn(uint256 currentSupply, uint256 tokensToSell) external pure returns (uint256);

    // State-changing functions
    function initializeBattle(BattleInitParams calldata params) external;
    function buyShares(
        uint64 battleId,
        bool artistA,
        uint256 amount,
        uint256 minTokensOut,
        uint64 deadline
    ) external payable returns (uint256 tokensMinted);
    function sellShares(
        uint64 battleId,
        bool artistA,
        uint256 tokenAmount,
        uint256 minAmountOut,
        uint64 deadline
    ) external returns (uint256 amountReceived);
    function endBattle(uint64 battleId, bool winnerIsArtistA) external;
    function claimShares(uint64 battleId) external returns (uint256 amountReceived);
}

contracts/src/EphemeralBattleToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

/**
 * @title EphemeralBattleToken
 * @notice Minimal ERC20 token for battle shares - destroyed at settlement
 * @dev Only the WaveWarz contract can mint/burn tokens
 */
contract EphemeralBattleToken is ERC20 {
    address public immutable battleContract;
    uint64 public immutable battleId;
    bool public immutable isArtistA;

    error OnlyBattleContract();

    modifier onlyBattleContract() {
        if (msg.sender != battleContract) revert OnlyBattleContract();
        _;
    }

    constructor(
        uint64 _battleId,
        bool _isArtistA,
        string memory name,
        string memory symbol
    ) ERC20(name, symbol) {
        battleContract = msg.sender;
        battleId = _battleId;
        isArtistA = _isArtistA;
    }

    /**
     * @notice Mint tokens to a trader (only callable by battle contract)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyBattleContract {
        _mint(to, amount);
    }

    /**
     * @notice Burn tokens from a trader (only callable by battle contract)
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     */
    function burn(address from, uint256 amount) external onlyBattleContract {
        _burn(from, amount);
    }

    /**
     * @notice Get token balance for a trader
     * @param account Address to check balance
     * @return Balance of tokens
     */
    function balanceOf(address account) public view override returns (uint256) {
        return super.balanceOf(account);
    }

    /**
     * @notice Decimals for the token (18 like ETH)
     */
    function decimals() public pure override returns (uint8) {
        return 18;
    }
}

